)VALUES(
			#{uid},#{gid},#{num},
			#{createdUser},#{createdTime},
			#{modifiedUser},#{modifiedTime}
)

mapper.select:
SELECT cid,gid,title, image,price,t_cart.num FROM t_cart INNER JOIN t_goods ON t_cart.gid=t_goods.id WHERE cid IN (
#{cid}
) ORDER BY t_cart.created_time DESC
